--
-- Name: log_isdn_change(); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public.log_isdn_change() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
declare 
	
	name_field varchar :='';
	value_before varchar :='';
	value_after varchar :='';
	change_by varchar :='';
	change_on timestamp(0) := current_timestamp;
begin 
	if (old.shop_id <> new.shop_id)
	then name_field := concat(name_field,'shop_id',',');
		value_before := concat(value_before, old.shop_id,',');
		value_after := concat(value_after,new.shop_id,',');
	end if;
	if (old.status_id <> new.status_id)
	then name_field := concat(name_field,'status_id',',');
		value_before := concat(value_before, old.status_id,',');
		value_after := concat(value_after,new.status_id,',');
	end if;
	if (old.role_id <> new.role_id)
	then name_field := concat(name_field,'role_id',',');
		value_before := concat(value_before, old.role_id,',');
		value_after := concat(value_after,new.role_id,',');
	end if;
	if (old.price_custom <> new.price_custom)
	then name_field := concat(name_field,'price_custom',',');
		value_before := concat(value_before, old.price_custom,',');
		value_after := concat(value_after,new.price_custom,',');
	end if;
	if (old.price_default <> new.price_default)
	then name_field := concat(name_field,'price_default',',');
		value_before := concat(value_before, old.price_default,',');
		value_after := concat(value_after,new.price_default,',');
	end if;
	if (old.sold <> new.sold)
	then name_field := concat(name_field,'sold',',');
		value_before := concat(value_before, old.sold,',');
		value_after := concat(value_after,new.sold,',');
	end if;
	if (old.object_holding <> new.object_holding)
	then name_field := concat(name_field,'object_holding',',');
		value_before := concat(value_before, old.object_holding,',');
		value_after := concat(value_after,new.object_holding,',');
	end if;
	if (old."level" <> new."level")
	then name_field := concat(name_field,'level',',');
		value_before := concat(value_before, old."level",',');
		value_after := concat(value_after,new."level",',');
	end if;
	if(new.update_by is null)
		then change_by := session_user;
	elsif(old.update_by<>new.update_by)
		then change_by := concat(new.update_by,'(',session_user,')');
	else 
		change_by := concat(new.update_by,'(',session_user,')^');
	end if;
	insert into inventory.isdn_his_new (isdn,name_field,value_before,value_after,change_on,change_by) values (new.isdn,name_field ,value_before ,value_after,change_on, change_by);
	return new;
end;
$$;


