-- Name: to_latinh(character varying); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public.to_latinh(character varying) RETURNS character varying
    LANGUAGE plpgsql
    AS $_$
declare output varchar := $1;
begin 
	if (output is null)
	then return null;
	elsif (output = '')
	then return '';
	end if;
	output := regexp_replace(output, 'Ô|Ồ|Ố|Ổ|Ỗ|Ộ|Ơ|Ờ|Ớ|Ở|Ỡ|Ợ|Ò|Ó|Ỏ|Õ|Ọ','O','g');
	output := regexp_replace(output, 'Ê|Ề|Ế|Ể|Ễ|Ệ|È|É|Ẻ|Ẽ|Ẹ','E','g');
	output := regexp_replace(output, 'Ư|Ừ|Ứ|Ử|Ữ|Ự|Ù|Ú|Ủ|Ũ|Ụ','U','g');
	output := regexp_replace(output, 'Í|Ì|Í|Ỉ|Ĩ|Ị','I','g');
	output := regexp_replace(output, 'Ỳ|Ý|Ỷ|Ỹ|Ỵ','Y','g');
	output := regexp_replace(output, 'À|Á|Ả|Ã|Ạ|Ă|Ẳ|Ằ|Ắ|Ẵ|Ặ|Â|Ầ|Ấ|Ẩ|Ẫ|Ậ','A','g');
	output := regexp_replace(output, 'à|á|ả|ã|ạ|ă|ằ|ắ|ẳ|ẵ|ặ|â|ầ|ấ|ẩ|ẫ|ậ','a','g');
	output := regexp_replace(output, 'ò|ó|ỏ|õ|ọ|ô|ồ|ố|ổ|ỗ|ộ|ơ|ờ|ớ|ở|ỡ|ợ','o','g');
	output := regexp_replace(output, 'ê|ề|ế|ể|ễ|ệ|è|é|ẻ|ẽ|ẹ','e','g');
	output := regexp_replace(output, 'ư|ừ|ứ|ử|ữ|ự|ù|ú|ủ|ũ|ụ','u','g');
	output := regexp_replace(output, 'ì|í|ỉ|ĩ|ị','i','g');
	output := regexp_replace(output, 'ỳ|ý|ỷ|ỹ|ỵ','y','g');
	output := regexp_replace(output, 'đ','d','g');
output := regexp_replace(output, 'Đ','d','g');
	return output;
end
$_$;


