--
-- Name: createRO(character varying, bigint, character varying, character varying, character varying, bigint, bigint, bigint, bigint, bigint, character varying, character varying, character varying, character varying, character varying, bigint, character varying, bigint, bigint, bigint, bigint, character varying, bigint, character varying, character varying, character varying, bigint, bigint, character varying, character varying); Type: PROCEDURE; Schema: sale; Owner: postgres
--

CREATE PROCEDURE sale."createRO"(_ordercode character varying, _customerid bigint, _custname character varying, _isdn character varying, _packagecode character varying, _callinamount bigint, _calloutamount bigint, _smsinamount bigint, _smsoutamount bigint, _dataamount bigint, _phone character varying, _address character varying, _province character varying, _district character varying, _block character varying, _receivetype bigint, _receivetypename character varying, _paymenttype bigint, _totalisdn bigint, _deliveryfee bigint, _discountisdn bigint, _vouchercode character varying, _referenceid bigint, _portraitimg character varying, _idfrontimg character varying, _idbackimg character varying, _deliverytype bigint, _shopid bigint, _time character varying, INOUT o_error character varying)
    LANGUAGE plpgsql
    AS $$
DECLARE
    _count         INT;
    _isdnType      inventory.isdn;
    _checkoutType  payment.payment_checkout;
    _status        INT     := 1;
    _statusType    sale.retail_order_status;
    _vDiscount     int8    := 0;
    _voucherType   voucher.voucher;
    _isPaid        BOOLEAN := FALSE;
    _paidAt        VARCHAR;
    _totalOrder    int8    := 0;
    _totalFee      int8    := 0;
    _totalDiscount int8    := 0;
    _total         int8    := 0;
    _createdId     int8;
BEGIN
    o_error := '0';
--check duplicate
    RAISE NOTICE 'Checking Duplicate Order Code: %',_orderCode;
    SELECT COUNT(*) INTO _count FROM sale.retail_order WHERE code = _orderCode;
    IF (_count > 0) THEN
        RAISE NOTICE 'Order Duplicated';
        o_error := '515';
        RETURN;
    END IF;

--check order
    RAISE NOTICE 'Checking Order Code: %',_orderCode;
    IF (_orderCode IS NULL OR _orderCode = '') THEN
        RAISE NOTICE 'Order Code Null';
        o_error := '6';
        RETURN;
    ELSE
        SELECT * INTO _checkoutType FROM payment.payment_checkout A WHERE A.order_code = _orderCode LIMIT 1;
    END IF;

    IF (_checkoutType IS NULL) THEN
        RAISE NOTICE 'Order Code % Not Found', _ordercode;
        o_error := '6';
        RETURN;
    END IF;

--check payment
    IF (_paymentType = 1) THEN-- pre_paid
        IF (_checkoutType.trans_status = '00') THEN-- success
            _isPaid := TRUE;
            _paidAt := _checkoutType.paid_at;
        ELSE
            RAISE NOTICE 'Order Code % Not Paid', _ordercode;
            o_error := '546';
            RETURN;
        END IF;
    ELSE
        _paymentType := 2;-- cod
    END IF;

--check isdn
    RAISE NOTICE 'Checking ISDN: %',_isdn;
    SELECT * INTO _isdnType FROM inventory.isdn WHERE isdn = _isdn AND status_id = 1 LIMIT 1;

    IF (_isdnType IS NULL) THEN
        RAISE NOTICE 'ISDN % is not avaliable', _isdn;
        o_error := '503';
        RETURN;
    ELSE
        UPDATE inventory.isdn SET status_id = 5 WHERE isdn = _isdn AND status_id = 1;
    END IF;

--check voucher
    IF (_customerId <> 0 AND _voucherCode IS NOT NULL AND _voucherCode <> '') THEN
        RAISE NOTICE 'Checking Voucher: %', _voucherCode;

        SELECT *
        INTO _voucherType
        FROM voucher.voucher
        WHERE code = _voucherCode
          AND status = 1
          AND amount_used < amount
        LIMIT 1;

        IF (_voucherType IS NULL) THEN
            RAISE NOTICE 'Voucher Code % Not Found',_voucherCode;
            o_error := '519';
            ROLLBACK;
            RETURN;
        END IF;

        RAISE NOTICE 'Checking Voucher % For Customer', _voucherCode;
        SELECT COUNT(*)
        INTO _count
        FROM voucher.v_cust
        WHERE cust_id = _customerId
          AND v_code = _voucherCode
          AND validated_at <= _time
          AND expired_at >= _time
          AND amount > 0;

        IF (_count = 0) THEN
            RAISE NOTICE 'Voucher Code % For Customer Not Found', _voucherCode;
            o_error := '519';
            ROLLBACK;
            RETURN;
        END IF;

        RAISE NOTICE 'Calculate discount from Voucher %',_voucherCode;
        UPDATE voucher.v_cust
        SET amount = amount - 1
        WHERE cust_id = _customerId
          AND v_code = _voucherCode
          AND validated_at <= _time
          AND expired_at >= _time
          AND amount > 0;

        UPDATE voucher.voucher SET amount_used = amount_used + 1 WHERE code = _voucherCode;

        INSERT INTO voucher.v_cust_his (v_id,
                                        v_code,
                                        v_value,
                                        cust_id,
                                        description,
                                        TIME)
        VALUES (_voucherType.ID,
                _voucherType.code,
                _voucherType.VALUE,
                _customerId,
                concat('Giảm giá cho đơn hàng ', _ordercode),
                _time);
        _vDiscount := _voucherType.VALUE;
    END IF;

-- calculate money
    _totalOrder := _totalisdn;
    _totalFee := _deliveryFee;
    _totalDiscount := _discountisdn + _vDiscount;
    _total := _totalOrder + _totalFee - _totalDiscount;

-- insert order
    INSERT INTO "sale"."retail_order" ("address",
                                       "block",
                                       "call_in_amount",
                                       "call_out_amount",
                                       "code",
                                       "created_at",
                                       "data_amount",
                                       "delivered_at",
                                       "m_delivery_fee",
                                       "m_discount_isdn",
                                       "m_discount_voucher",
                                       "district",
                                       "id_back_img",
                                       "id_front_img",
                                       "is_paid",
                                       "isdn",
                                       "package_code",
                                       "paid_at",
                                       "payment_src",
                                       "payment_src_name",
                                       "payment_type",
                                       "phone",
                                       "portrait_img",
                                       "province",
                                       "receive_type",
                                       "receive_type_name",
                                       "reference_id",
                                       "sms_in_amount",
                                       "sms_out_amount",
                                       "m_total",
                                       "m_total_discount",
                                       "m_total_fee",
                                       "m_total_isdn",
                                       "m_total_order",
                                       "voucher_code",
                                       "customer_id",
                                       "status",
                                       "customer_name",
                                       "delivery_type",
                                       "shop_id")
    VALUES (_address,
            _block,
            _callInAmount,
            _callOutAmount,
            _ordercode,
            _time,
            _dataAmount,
            '',
            _deliveryfee,
            _discountisdn,
            _vDiscount,
            _district,
            _idbackimg,
            _idfrontimg,
            _isPaid,
            _isdn,
            _packagecode,
            _paidAt,
            _paymenttype,
            _checkoutType.method_code,
            _checkoutType.method_code,
            _phone,
            _portraitimg,
            _province,
            _receivetype,
            _receiveTypeName,
            _referenceid,
            _smsInAmount,
            _smsOutAmount,
            _total,
            _totalDiscount,
            _totalFee,
            _totalisdn,
            _totalOrder,
            _vouchercode,
            _customerid,
            _status,
            _custname,
            _deliverytype,
            _shopid)
    RETURNING order_id INTO _createdId;

-- insert order tracking
    SELECT * INTO _statusType FROM sale.retail_order_status WHERE ID = _status LIMIT 1;
    IF (_statusType IS NOT NULL) THEN
        INSERT INTO "sale"."retail_order_tracking" ("description",
                                                    "order_id",
                                                    "status",
                                                    "status_description",
                                                    "status_name",
                                                    "time",
                                                    "icon")
        VALUES (concat('Mua sim số ', _isdn),
                _createdId,
                _status,
                _statusType.description,
                _statusType.NAME,
                _time,
                _statusType.icon);
    ELSE
        INSERT INTO "sale"."retail_order_tracking" ("description", "order_id", "status", "time")
        VALUES (concat('Mua sim số ', _isdn),
                _createdId,
                _status,
                _time);

    END IF;

    IF (_isPaid) THEN
        _status := 2;
        SELECT * INTO _statusType FROM sale.retail_order_status WHERE ID = _status LIMIT 1;
        IF (_statusType IS NOT NULL) THEN
            INSERT INTO "sale"."retail_order_tracking" ("description",
                                                        "order_id",
                                                        "status",
                                                        "status_description",
                                                        "status_name",
                                                        "time",
                                                        "icon")
            VALUES ('Thanh toán thành công',
                    _createdId,
                    _status,
                    _statusType.description,
                    _statusType.NAME,
                    _time,
                    _statusType.icon);
        ELSE
            INSERT INTO "sale"."retail_order_tracking" ("description", "order_id", "status", "time")
            VALUES ('Thanh toán thành công',
                    _createdId,
                    _status,
                    _time);
        END IF;
    END IF;
    COMMIT;
    RETURN;

END;
$$;


